import React, { useEffect, useState } from "react";
import { BsGeoAlt, RiArrowDropDownLine } from "../../styles/Icons";
import { useSelector } from "react-redux";
import {
  Container,
  LocationInfo,
  SearchWrapper,
  SearchTitle,
  SearchBarContent,
  DropDownMenu,
  SearchIcon,
  DropDownContainer,
  SearchBar,
  Row,
  UserInfo,
} from "./styles";

function Header() {
  const [city, getCity] = useState({});
  const [open, setOpen] = useState(false);
  const dropdownRef = useRef(null);
  const user_data = useSelector((state) => state.user_data);

  useEffect(() => {
    const pageClick = (e) => {
      console.log(e);
    };

    if (open) {
      window.addEventListener("click", pageClick);
    }

    return () => {
      window.removeEventListener("click", pageClick);
    };

    if (dropdownRef.current !== null && !dropdownRef.current.contains(e.target)) {
      setIsActive(!isActive);
    }
  }, [open]);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(function (position) {
      fetch(
        `http://api.positionstack.com/v1/forward?access_key=67dca1793150fc082092dbe9dbc589a1&query=${position.coords.latitude},${position.coords.longitude}&output=json`
      ).then((response) => {
        getCity(response.data);
      });
    });
  }, []);

  return (
    <Container>
      <LocationInfo>
        <BsGeoAlt size={25} />
        <div>
          <p>Enviar para</p>
          <h3>Uberaba 38082-221</h3>
        </div>
      </LocationInfo>
      <SearchWrapper>
        <SearchBar placeholder="Busque por produtos aqui..." />
        <div className="separator"></div>
        <SearchIcon />
      </SearchWrapper>
      <Row>
        <UserInfo>
          {user_data.logged_in ? (
            <>
              <p>OlÃ¡ {user_data.username}</p>
              <div onClick={() => setOpen(!open)}>
                <strong>Sua conta</strong>
                <RiArrowDropDownLine size={30} />
              </div>
            </>
          ) : (
            <>
              <div onClick={() => setOpen(!open)}>
                <strong>Entre com sua conta</strong>
                <RiArrowDropDownLine size={30} />
              </div>
            </>
          )}
          <DropDownContainer ref={dropdownRef}>
            {open && (
              <DropDownMenu>
                <h1>foda</h1>
              </DropDownMenu>
            )}
          </DropDownContainer>
        </UserInfo>
      </Row>
    </Container>
  );
}

export default Header;
