import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { BsGeoAlt, RiTruckLine } from "../../styles/Icons";
import Rating from "@material-ui/lab/Rating";
import cep from "cep-promise";
import ProductCard from "../../components/ProductCard";
import {
  Container,
  PictureWrapper,
  ProductInfo,
  SellerInfoWrapper,
  SellerInfo,
  Picture,
  DeliveryInfo,
  BuyNowBtn,
  AddToCartBtn,
  Delivery,
  Description,
  BuyBtns,
  SimilarProducts,
  ProductsList,
} from "./styles";

function DetailsPage() {
  const [productData, setProductData] = useState({});
  const [similarProducts, setSimilarProducts] = useState({ data: [] });
  const [logged, setLogged] = useState(false);
  const [userData, setUserData] = useState({});
  const [locationInfo, setLocationInfo] = useState({});
  const [buyQuantity, setBuyQuantity] = useState(1);
  const { productId } = useParams();
  const [isDesktop, setDesktop] = useState(window.innerWidth > 1000);

  const updateMedia = () => {
    setDesktop(window.innerWidth > 1450);
  };

  useEffect(() => {
    window.addEventListener("resize", updateMedia);
    return () => window.removeEventListener("resize", updateMedia);
  });

  useEffect(() => {
    fetch(`http://localhost:3000/product/consult?id=${productId}`, {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 404:
          console.log("Produto não encontrado");
          break;
        case 200:
          setProductData(data);
          break;
      }
    });
  }, []);

  useEffect(() => {
    fetch("http://localhost:3000/auth/consult", {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.getItem("access_token")}`,
      },
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 200:
          setLogged(!logged);
          setUserData(data);
          break;
      }
    });
  }, []);

  useEffect(() => {
    fetch(
      `http://localhost:3000/product/consult_similar_products?category=${productData.category}&id=${productId}`,
      {
        method: "get",
      }
    ).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 200:
          setSimilarProducts({ data: data });
          break;
      }
    });
  }, [productData]);

  function addToCart() {
    if (logged) {
      fetch("http://localhost:3000/cart/addToCart", {
        method: "post",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("access_token")}`,
        },
        body: JSON.stringify({
          productId: productId,
          quantity: buyQuantity,
        }),
      }).then(async (res) => {
        switch (res.status) {
          case 200:
            alert("Item adicionado ao carrinho!", res);
            break;
        }
      });
    } else {
      alert("Logue para usar o carrinho!");
    }
  }

  useEffect(() => {
    cep(userData.cep).then((res) => {
      setLocationInfo(res);
    });
  }, [userData]);

  return (
    <Container>
      <PictureWrapper>
        {document.window.innerWidth > 1000 ? (
          <Picture
            enlargedImageContainerClassName="large-image"
            hoverDelayInMs={0}
            {...{
              smallImage: {
                src: productData.image,
                width: 480,
                height: 480,
              },
              largeImage: {
                src: productData.image,
                width: 1200,
                height: 1800,
              },
            }}
          />
        ) : (
          <img src={productData.image} />
        )}
      </PictureWrapper>
      <ProductInfo>
        <h2>{productData.name}</h2>
        <Rating
          name="half-rating"
          defaultValue={productData.rating}
          precision={0.5}
          readOnly
        />
        <h1>R$ {productData.price}</h1>
        <h4>{`em ${productData.portion}x R$${(
          productData.price / productData.portion
        ).toFixed(2)}`}</h4>
        <hr />
        <ul>
          <li>
            <strong>Marca: </strong>
            <span>{productData.brand}</span>
          </li>
          <li>
            <strong>Categoria: </strong>
            <span>{productData.category}</span>
          </li>
          <li>
            <strong>Tags: </strong>
            {productData.tags != undefined ? (
              productData.tags.map((t, index) => {
                return <span key={`${index}`}>{(index ? ", " : "") + t}</span>;
              })
            ) : (
              <></>
            )}
          </li>
        </ul>
      </ProductInfo>
      <SellerInfoWrapper>
        <SellerInfo>
          <ul>
            <li>
              <h2>R$ {productData.price}</h2>
            </li>
            <li>
              {productData.deliveryPrice > 0 ? (
                <DeliveryInfo>
                  <RiTruckLine size={25} />
                  <p>Entrega: R$ {productData.deliveryPrice}</p>
                </DeliveryInfo>
              ) : (
                <DeliveryInfo>
                  <RiTruckLine size={25} fill="#d7263d" />
                  <p>
                    Entrega <a>GRÁTIS</a>
                  </p>
                </DeliveryInfo>
              )}
            </li>
            <li>
              <DeliveryInfo>
                <BsGeoAlt size={20} />
                {logged ? (
                  <Delivery>
                    {userData.cep ? (
                      <>
                        <p>Enviar para {userData.name},</p>
                        <p>
                          <b>{locationInfo.city}</b> {locationInfo.cep}
                        </p>
                      </>
                    ) : (
                      <p>Cadastre um endereço!</p>
                    )}
                  </Delivery>
                ) : (
                  <p>
                    <Link to="/login">Entre ou se cadastre!</Link>
                  </p>
                )}
              </DeliveryInfo>
            </li>
            <li>
              {productData.countInStock > 0 ? (
                <h2>Em estoque!</h2>
              ) : (
                <h2>Fora de estoque.</h2>
              )}
            </li>
            <li>
              {productData.countInStock > 0 ? (
                <>
                  <strong>Quantidade:</strong>
                  <select
                    onChange={(e) => setBuyQuantity(e.target.value)}
                    className="quantity"
                  >
                    {Array.from(Array(productData.countInStock).keys()).map(
                      (i) => {
                        return i > 0 && i < 6 && <option value={i}>{i}</option>;
                      }
                    )}
                  </select>
                  <sub>({productData.countInStock} em estoque!)</sub>
                </>
              ) : (
                <></>
              )}
            </li>
            <li>
              <BuyBtns>
                <BuyNowBtn type="button" value="Comprar agora" />
                <AddToCartBtn
                  type="button"
                  value="Adicionar ao carrinho"
                  onClick={() => addToCart()}
                />
              </BuyBtns>
            </li>
          </ul>
        </SellerInfo>
      </SellerInfoWrapper>
      <Description>{productData.description}</Description>
      <SimilarProducts>
        <h3>Produtos semelhantes que você pode gostar:</h3>
        <ProductsList>
          {similarProducts.data.slice(0, 4).map((p) => {
            return (
              <li>
                <ProductCard
                  key={p._id}
                  redirect={() => (window.location.href = p._id)}
                  imgsrc={p.image}
                  title={p.name}
                  brand={p.brand}
                  price={`R$ ${p.price}`}
                  portion={`${p.portion}x R$${(p.price / p.portion).toFixed(
                    2
                  )} sem juros`}
                />
              </li>
            );
          })}
        </ProductsList>
      </SimilarProducts>
    </Container>
  );
}

export default DetailsPage;
