import React, { useEffect, useState, useRef } from "react";
import { BsGeoAlt, RiArrowDropDownLine } from "../../styles/Icons";
import { useSelector } from "react-redux";
import {Link} from 'react-router-dom'
import {
  Container,
  LocationInfo,
  SearchWrapper,
  SearchTitle,
  SearchBarContent,
  ProfilePicture,
  UserProfile,
  DropDownMenu,
  DropDownItem,
  SearchIcon,
  DropDownContainer,
  SearchBar,
  Row,
  UserInfo,
} from "./styles";
import { useHistory } from "react-router";

function Header() {
  const [city, getCity] = useState({});
  const [open, setOpen] = useState(false);
  const dropdownRef = useRef(null);
  const user_data = useSelector((state) => state.user_data);
  let history = useHistory();

  useEffect(() => {
    const pageClick = (e) => {
      if (
        dropdownRef.current !== null &&
        !dropdownRef.current.contains(e.target)
      ) {
        setOpen(!open);
      }
    };

    if (open) {
      window.addEventListener("click", pageClick);
    }

    return () => {
      window.removeEventListener("click", pageClick);
    };
  }, [open]);

  function logout() {
    localStorage.removeItem('access_token')
    localStorage.clear()
    if (localStorage.getItem('access_token') === null) {
      window.location.href = "/"
    }
  }

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(function (position) {
      fetch(
        `http://api.positionstack.com/v1/forward?access_key=67dca1793150fc082092dbe9dbc589a1&query=${position.coords.latitude},${position.coords.longitude}&output=json`
      ).then((response) => {
        getCity(response.data);
      });
    });
  }, []);

  return (
    <Container>
      <LocationInfo>
        <BsGeoAlt size={25} />
        <div>
          <p>Enviar para</p>
          <h3>Uberaba 38082-221</h3>
        </div>
      </LocationInfo>
      <SearchWrapper>
        <SearchBar placeholder="Busque por produtos aqui..." />
        <div className="separator"></div>
        <SearchIcon />
      </SearchWrapper>
      <Row>
        <UserInfo>
          {localStorage.getItem('access_token') ? (
            <>
              <p>OlÃ¡ {localStorage.getItem('logged_username')}</p>
              <div className="userinfo_div" onClick={() => setOpen(!open)}>
                <strong>Sua conta</strong>
                <RiArrowDropDownLine size={30} />
              </div>
            </>
          ) : (
            <>
              <div className="userinfo_div" onClick={() => setOpen(!open)}>
                <strong>Entre com sua conta</strong>
                <RiArrowDropDownLine size={30} />
              </div>
            </>
          )}
          <DropDownContainer ref={dropdownRef}>
            {open && (
              <DropDownMenu>
                {localStorage.getItem('access_token') ? (
                  <UserProfile>
                    <ProfilePicture picture={user_data.picture} />
                    <div className="wrapper">
                      <h3>OlÃ¡ {localStorage.getItem('logged_username')} ðŸ‘‹ </h3>
                      <a onClick={() => logout()}>Sair da conta</a>
                    </div>
                  </UserProfile>
                ) : (
                  <UserProfile>
                    <ProfilePicture picture="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSP5IU8GgSurLLlvDn7J82C_uQHCfNHaxEqP7aU2P8xSx5QfhTIZVqHYkT-g7bggRhu92w&usqp=CAU" />
                    <div className="wrapper">
                      <h3>OlÃ¡ ðŸ‘‹ </h3>
                      <Link to="/login">Entre ou se registre!</Link>
                    </div>
                  </UserProfile>
                )}
                <DropDownItem>
                  <a>Sua conta</a>
                </DropDownItem>
                <DropDownItem>
                  <a>Seus pedidos</a>
                </DropDownItem>
                <DropDownItem>
                  <a>SeguranÃ§a</a>
                </DropDownItem>
                <DropDownItem>
                  <a>Privacidade</a>
                </DropDownItem>
                <DropDownItem>
                  <a>Sua lista de desejos</a>
                </DropDownItem>
                <DropDownItem>
                  <a>Vender</a>
                </DropDownItem>
              </DropDownMenu>
            )}
          </DropDownContainer>
        </UserInfo>
      </Row>
    </Container>
  );
}

export default Header;
