import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { BsGeoAlt } from "../../styles/Icons";
import Rating from "@material-ui/lab/Rating";
import {cep} from 'cep-promise';
import {
  Container,
  PictureWrapper,
  ProductInfo,
  SellerInfoWrapper,
  SellerInfo,
  Picture,
  DeliveryInfo,
  BuyNowBtn,
  AddToCartBtn,
} from "./styles";

function DetailsPage() {
  const [productData, setProductData] = useState({});
  const [logged, setLogged] = useState(false);
  const [userData, setUserData] = useState({});
  const { productId } = useParams();

  useEffect(() => {
    fetch(`http://localhost:3000/product/consult?id=${productId}`, {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 404:
          console.log("Produto não encontrado");
          break;
        case 200:
          setProductData(data);
          break;
      }
    });
  }, []);

  useEffect(() => {
    fetch("http://localhost:3000/auth/consult", {
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.getItem("access_token")}`,
      },
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 200:
          setLogged(!logged);
          setUserData(data);
          break;
      }
    });
  }, []);

  return (
    <Container>
      <PictureWrapper>
        <Picture src={productData.image} />
      </PictureWrapper>
      <ProductInfo>
        <h2>{productData.name}</h2>
        <Rating name="half-rating" defaultValue={productData.rating} />
        <h1>R$ {productData.price}</h1>
        <h4>{`em ${productData.portion}x R$${(
          productData.price / productData.portion
        ).toFixed(2)}`}</h4>
        <hr />
        <span>
          <strong>Marca: </strong>
          <p>{productData.brand}</p>
        </span>
      </ProductInfo>
      <SellerInfoWrapper>
        <SellerInfo>
          <ul>
            <li>
              <h2>R$ {productData.price}</h2>
            </li>
            <li>
              {productData.deliveryPrice > 0 ? (
                <>
                  <p>Entrega: R$ {productData.deliveryPrice}</p>
                </>
              ) : (
                <>
                  <p>
                    Entrega <a>GRÁTIS</a>
                  </p>
                </>
              )}
            </li>
            <li>
              <DeliveryInfo>
                <BsGeoAlt size={20} />
                {logged ? (
                  <p>
                    Enviar para {userData.name}, {cep(38082221).then({city})}
                  </p>
                ) : (
                  <p>
                    <Link to="/login">Entre ou se cadastre!</Link>
                  </p>
                )}
              </DeliveryInfo>
            </li>
            <li>
              {productData.countInStock > 0 ? (
                <h2>Em estoque!</h2>
              ) : (
                <h2>Fora de estoque.</h2>
              )}
            </li>
            <li>
              <BuyNowBtn type="button" value="Comprar agora" />
              <AddToCartBtn type="button" value="Adicionar ao carrinho" />
            </li>
          </ul>
        </SellerInfo>
      </SellerInfoWrapper>
    </Container>
  );
}

export default DetailsPage;
