import React, { useEffect, useState, useRef } from "react";
import { BsGeoAlt, RiArrowDropDownLine } from "../../styles/Icons";
import { useSelector } from "react-redux";
import {
  Container,
  LocationInfo,
  SearchWrapper,
  SearchTitle,
  SearchBarContent,
  ProfilePicture,
  UserProfile,
  DropDownMenu,
  DropDownItem,
  SearchIcon,
  DropDownContainer,
  SearchBar,
  Row,
  UserInfo,
} from "./styles";

function Header() {
  const [city, getCity] = useState({});
  const [open, setOpen] = useState(false);
  const dropdownRef = useRef(null);
  const user_data = useSelector((state) => state.user_data);

  useEffect(() => {
    const pageClick = (e) => {
      if (
        dropdownRef.current !== null &&
        !dropdownRef.current.contains(e.target)
      ) {
        setOpen(!open);
      }
    };

    if (open) {
      window.addEventListener("click", pageClick);
    }

    return () => {
      window.removeEventListener("click", pageClick);
    };
  }, [open]);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(function (position) {
      fetch(
        `http://api.positionstack.com/v1/forward?access_key=67dca1793150fc082092dbe9dbc589a1&query=${position.coords.latitude},${position.coords.longitude}&output=json`
      ).then((response) => {
        getCity(response.data);
      });
    });
  }, []);

  return (
    <Container>
      <LocationInfo>
        <BsGeoAlt size={25} />
        <div>
          <p>Enviar para</p>
          <h3>Uberaba 38082-221</h3>
        </div>
      </LocationInfo>
      <SearchWrapper>
        <SearchBar placeholder="Busque por produtos aqui..." />
        <div className="separator"></div>
        <SearchIcon />
      </SearchWrapper>
      <Row>
        <UserInfo>
          {user_data.logged_in ? (
            <>
              <p>OlÃ¡ {user_data.username}</p>
              <div className="userinfo_div" onClick={() => setOpen(!open)}>
                <strong>Sua conta</strong>
                <RiArrowDropDownLine size={30} />
              </div>
            </>
          ) : (
            <>
              <div className="userinfo_div" onClick={() => setOpen(!open)}>
                <strong>Entre com sua conta</strong>
                <RiArrowDropDownLine size={30} />
              </div>
            </>
          )}
          <DropDownContainer ref={dropdownRef}>
            {open && (
              <DropDownMenu>
                {user_data.logged_in ? (
                  <UserProfile>
                    <ProfilePicture picture={user_data.picture} />
                    <div className="wrapper">
                      <h3>OlÃ¡ {user_data.username} ğŸ‘‹ </h3>
                      <a>Sair da conta</a>
                    </div>
                  </UserProfile>
                ) : (
                  <UserProfile>
                    <ProfilePicture picture="http://norapc.org/wp-content/uploads/2015/07/avatar-blank.png" />
                    <div className="wrapper">
                      <h3>OlÃ¡ ğŸ‘‹ </h3>
                      <a>Entre ou se registre!</a>
                    </div>
                  </UserProfile>
                )}
                <DropDownItem>
                  <a>Sua conta</a>
                </DropDownItem>
                <DropDownItem>
                  <a>Seus pedidos</a>
                </DropDownItem>
                <DropDownItem>
                  <a>SeguranÃ§a</a>
                </DropDownItem>
                <DropDownItem>
                  <a>Privacidade</a>
                </DropDownItem>
                <DropDownItem>
                  <a>Sua lista de desejos</a>
                </DropDownItem>
                <DropDownItem>
                  <a>Vender</a>
                </DropDownItem>
              </DropDownMenu>
            )}
          </DropDownContainer>
        </UserInfo>
      </Row>
    </Container>
  );
}

export default Header;
