import React, { useState } from "react";
import { useDispatch } from "react-redux";
import {
  Container,
  LoginWrapper,
  LoginInput,
  CreateAccountButton,
  LoginButton,
} from "./styles";

function UserLogin() {
  const [userEmail, setUserEmail] = useState("");
  const [userPass, setUserPass] = useState("");

  function authenticateUser() {
    fetch("http://localhost:3000/auth/authenticate", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        email: userEmail,
        password: userPass,
      }),
    }).then(async (res) => {
      let data = await res.json();

      switch (res.status) {
        case 404:
          console.log("Usu치rio n칚o encontrado");
          break;
        case 403:
          console.log("Senha inv치lida");
          break;
        case 200:
          if (data.token) {
            userLogin(data.check_user.name, data.token);
          }
          break;
      }
    });
  }

  function userLogin(username, token) {
    localStorage.setItem("access_token", token);
    localStorage.setItem("logged_username", username);

    window.location.href = "/";
  }

  return (
    <Container>
      <LoginWrapper>
        <h2>Ol치 游녦, digite seu email e senha cadastrado!</h2>
        <p>Email</p>
        <LoginInput
          type="text"
          onChange={(e) => setUserEmail(e.target.value)}
        />
        <p>Senha</p>
        <LoginInput
          type="password"
          onChange={(e) => setUserPass(e.target.value)}
        />
        <LoginButton
          type="button"
          value="Entrar"
          onClick={() => authenticateUser()}
        />
        <CreateAccountButton type="button" value="Crie sua conta" onClick={() => window.location.href = "/register"} />
      </LoginWrapper>
    </Container>
  );
}

export default UserLogin;
